#####################
### Set filepaths ###
#####################

iqtree.path        <- "/panfs/pfs.local/home/j926w878/programs/iqtree-1.6.10-Linux/bin/iqtree"
work.dir           <- "/panfs/pfs.local/home/j926w878/scratch/SequenceCapture/SnakeCap1/Alignments/target-only_untrimmed" ### directory path where gene alignments are found
target.folder.name <- "target-only_untrimmed"            ### folder name where gene trees will be saved
specific.files     <- paste()                            ### a character vector specifying the names of the specific alignment files to make trees for; if left as an empty vector, gene trees will be made for all alignments 

###########################################
## Usually dont change things below here ##
###########################################

##########
## creates additional directories for output files 

from.dir <- work.dir

setwd(from.dir)
setwd("..")

to.dir <- paste(getwd(),"/gene-trees/",target.folder.name,sep="")

if(file.exists(to.dir)==F){
	dir.create(to.dir)
}

setwd(to.dir)

logs.dir <- paste(getwd(),"/logfiles",sep="")
if(file.exists(logs.dir)==F){
	dir.create(logs.dir)
}

trees.dir <- paste(getwd(),"/treefiles",sep="")
if(file.exists(trees.dir)==F){
	dir.create(trees.dir)
}

extensions1 <- c("phy.bionj","phy.ckp.gz","phy.contree","phy.iqtree","phy.log","phy.mldist","phy.model.gz","phy.splits.nex",".phy.uniqueseq.phy")  ### file extensions indicating log files
extensions2 <- c("phy.treefile")                                                                                                                   ### file extensions indicating tree file

################
## runs IQTREE


alignment.files.short <- list.files(work.dir)
alignment.files       <- file.path(work.dir,alignment.files.short)

if(length(specific.files)!=0){                                                        ###|if specific.files is not empty, this makes sure 
	alignment.files <- alignment.files[alignment.files.short %in% specific.files] ###|iqtree only runs on those specific files
}

for(i in 1:length(alignment.files)){
	commandString <- paste(iqtree.path,"-s",alignment.files[i],"-pre",alignment.files[i],"-m TEST -bb 1000")
	system(commandString)
}

#######

files.short <- list.files(from.dir)
files.full  <- paste(from.dir,"/",files.short,sep="")

moveLogs.from <- files.full[unlist(lapply(X=extensions1,FUN=grep,files.short))]
moveLogs.to   <- paste(logs.dir,"/",files.short[unlist(lapply(X=extensions1,FUN=grep,files.short))],sep="")

moveTrees.from <- files.full[unlist(lapply(X=extensions2,FUN=grep,files.short))]
moveTrees.to   <- paste(trees.dir,"/",gsub(pattern=".phy.treefile",replacement="_phy.tre",files.short[unlist(lapply(X=extensions2,FUN=grep,files.short))]),sep="")


for(i in 1:length(moveLogs.from)){
	system(paste("mv",moveLogs.from[i],moveLogs.to[i],sep=" "))
}
for(i in 1:length(moveTrees.from)){
	system(paste("mv",moveTrees.from[i],moveTrees.to[i],sep=" "))
}


